package week1.day2;

public class LearnControlStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=7;
		int b=8;
		
		//7<8
		if(a>8) {
			System.out.println("b is less than a");
			
		}
		else {
			System.out.println("a is less than b");
		}

	}

}
