package week1.day2;

public class LearnElseIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=7;
		int b=8;
		
		//7<8
		if(a>b) {
			System.out.println("b is less than a");
			
		}
		else if(a<b){
			System.out.println("a is less than b");
		}
		else {
			System.out.println("a is equal to b");
		}
	}

}
