package week1.day2;

public class LearnForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// to print nos 1 to 5 
		//starting num 1
		//ending num 5
		
		for(int i=0;i<=5;i++) {
			
			System.out.println(i); // first time 1 will be printed 
		}
		System.out.println("----------------------------------");
		//To print nos 5 to 1
		// use reverse for loop 5 4 3 2 1
		//starting point 5 ending point=1
		
   for(int i=5;i>=1;i--) {
			
			System.out.println(i); // first time 1 will be printed 
		}

	}

}
